module.exports.gremlinStep = {
	creator: "Gremlin.defineStep('creator', [Vertex, Pipe], { _().in('CREATED') } );",
	memberOf: "Gremlin.defineStep('member_of', [Vertex, Pipe], { _().sideEffect{x = it}.in('MEMBER_OF').out('MEMBER_OF').out('USERS_REFERENCE').back(2) });",
	followers: "Gremlin.defineStep('followers', [Vertex, Pipe], { _().sideEffect{x = it}.in('FOLLOWS').out('MEMBER_OF').out('USERS_REFERENCE').back(2) });",
	comments: "Gremlin.defineStep('comments', [Vertex, Pipe], { _().sideEffect{x = it}.transform{[ it.in('COMMENTED_ON').toList(), it.inE('COMMENTED_ON').toList() ]} });",
	likes: "Gremlin.defineStep('likes', [Vertex, Pipe], { _().sideEffect{x = it}.transform{[ it.in('LIKES').toList(), it.inE('LIKES').toList() ]} });",
	tags: "Gremlin.defineStep('tags', [Vertex, Pipe], { _().sideEffect{x = it}.out('TAGGED_IN') });",
	usersIn: "Gremlin.defineStep('users_in', [Vertex, Pipe], { _().sideEffect{x = it}.in('TAGGED_IN').out('MEMBER_OF').out('USERS_REFERENCE').back(2) });",
	advMoments: "Gremlin.defineStep('adv_moments', [Vertex, Pipe], { _().sideEffect{x = it}.in('MEMBER_OF').out('MEMBER_OF').out('MOMENTS_REFERENCE').back(2).transform{[ it, it.creator.next(), it.comments.collect(), it.likes.collect(), it.tags.toList(), it.users_in.toList() ]} });",
	usrMoments: "Gremlin.defineStep('user_moments', [Vertex, Pipe], { _().sideEffect{x=it}.out('CREATED').out('MEMBER_OF').out('MOMENTS_REFERENCE').back(2).transform{[ it, it.creator.next(), it.comments.collect(), it.likes.collect(), it.tags.toList(), it.users_in.toList() ]} });",
	tagMoments: "Gremlin.defineStep('tag_moments', [Vertex, Pipe], { _().sideEffect{x=it}.in('TAGGED_IN').out('MEMBER_OF').out('MOMENTS_REFERENCE').back(2).transform{[ it, it.creator.next(), it.comments.collect(), it.likes.collect(), it.tags.toList(), it.users_in.toList() ]} });",
	advCollectionScript: ".transform{[ it, it.creator.next(), it.member_of.toList(), it.followers.toList() ]}",
	usrCollectionScript: ".transform{[ it, it.followers.toList() ]}",
	tagCollectionScript: ".transform{[ it, it.followers.toList() ]}"
};